import React from "react";
import Axios from "axios";
import "../App.css";

class TEST_Card extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      city: null,
      temp: null,
      list: [],
    };
  }

  componentDidMount() {
    this.getWeather();
  }

  getWeather(index) {
    Axios.get(
      "https://api.openweathermap.org/data/2.5/forecast?q=Lisbon&appid=c9f6971381650cd6d35447c475cce567&units=metric"
    )
      .then((response) => response.json())
      .then((response) => {
        console.log(response.data.list);
        this.setState({
          city: response.data.city.name,
          temp: response.data.list[0].main.temp,
        });
      });
  }

  render() {
    return <div className="App">{JSON.stringify(this.state.list)}</div>;
  }
}

export default TEST_Card;
