import "./App.css";
import WeatherCards from "./Components/weatherCards";
import { useEffect, useState } from "react";
import { Oval } from "react-loader-spinner";
import $ from "jquery";
import { CountryDropdown, RegionDropdown } from "react-country-region-selector";

const App = () => {
  const [weather, setWeather] = useState({});
  const [country, setCountry] = useState("PT");
  const [region, setRegion] = useState("Lisboa");
  const [time, setTime] = useState(0);

  const fetchWeather = async () => {
    console.log(country);
    await fetch(
      `http://api.openweathermap.org/data/2.5/forecast?q=${region},${country}&appid=c9f6971381650cd6d35447c475cce567&units=metric`
    )
      .then(async response => {
        const data = await response.json();

        if (!response.ok) {
          const error = (data && data.message) || response.statusText;
          return Promise.reject(error);
        }
        return data;
      })
      .catch((error) => {
        console.error('Error!', error);
      });
    console.log(res);

  };

  useEffect(() => {
    const getWeather = async () => {
      const weatherApi = await fetchWeather();
      setWeather(weatherApi);
    };
    getWeather();

    const interval = setInterval(() => {
      setTime(time + 1);
    }, 350000);
    return () => clearInterval(interval);
  }, [time, region]);

  return (
    <div className="App">
      <header>
        <h1 className="Title">WeatherApp</h1>
        <h2 className="SelectRegion">Select a region: </h2>
        <div>
          <CountryDropdown
            valueType="short"
            value={country}
            onChange={(val) => setCountry(val)}
          />
          <RegionDropdown
            countryValueType="short"
            country={country}
            value={region}
            onChange={(val) => setRegion(val)}
          />
        </div>
      </header>
      <div>
        {$.isEmptyObject(weather) ? (
          <Oval></Oval>
        ) : (
          <WeatherCards weather={weather} />
        )}
      </div>
    </div>
  );
};

export default App;
