import "./App.css";
import WeatherCard from "./Components/weatherCard";
import { useEffect, useState } from "react";
import $ from 'jquery';
import { Oval } from 'react-loader-spinner';

const App = () => {
  const [weather, setWeather] = useState({});
  const [icon, setIcon] = useState();
  useEffect(() => {
    const getWeather = async () => {
      const weatherApi = await fetchWeather();
      setWeather(weatherApi);
    };

    const getIcon = async () => {
      const icon = await fetchIcon();

    }



    getWeather();
  }, []);

  const fetchWeather = async () => {
    const res = await fetch(
      "http://api.openweathermap.org/data/2.5/forecast?q=lisbon&appid=c9f6971381650cd6d35447c475cce567&units=metric"
    );
    const data = await res.json();

    return data;
  };

  const fetchIcon = async () => {
    const res = await fetch(
      `http://openweathermap.org/img/wn/${weather.list[0].weather.icon}@2x.png`
    );
    console.log(res)

    return res;
  }


  return (
    <div className="App">
      <header>
        <h1 className="Title">WeatherApp</h1>
      </header>
      <div className="Hlist">
        <ul>
          <li>
            {$.isEmptyObject(weather) ? (
              <Oval/>
            ) : (
              <WeatherCard weather={weather} />
            )}
          </li>
        </ul>
      </div>
    </div>
  );
};

export default App;
