import React from "react";
import WeatherCard from "./weatherCard";

const weatherCards = ({ weather }) => {

  const findNextDay = () => {
    const weatherDate = weather.list[0].dt_txt;
    console.log(weatherDate);

    const date = new Date(weatherDate);
    console.log(date);

    if(date.getHours() === 0){
        date.setHours(-24);
    }

    return date;
  };

  return (
    <div>
        {}
      <ul>
        <li>
          <WeatherCard weather={weather} index={index}></WeatherCard>
        </li>
      </ul>
    </div>
  );
};

export default weatherCards;
