import "./App.css";
import WeatherCards from "./Components/weatherCards";
import { useEffect, useState } from "react";
import $ from "jquery";
import { Oval } from "react-loader-spinner";

const App = () => {
  const [weather, setWeather] = useState({});
  const [time, setTime] = useState(0);

  useEffect(() => {
    const getWeather = async () => {
      const weatherApi = await fetchWeather();
      setWeather(weatherApi);
    };

    getWeather();

    const interval = setInterval(() => {
      setTime(time + 1);
    }, 350000);
    return () => clearInterval(interval);
  }, [time]);

  const fetchWeather = async () => {
    const res = await fetch(
      "http://api.openweathermap.org/data/2.5/forecast?q=lisbon&appid=c9f6971381650cd6d35447c475cce567&units=metric"
    );
    const data = await res.json();
    console.log(data);
    return data;
  };

  return (
    <div className="App">
      <header>
        <h1 className="Title">WeatherApp</h1>
        <h2>{weather.city.name}</h2>
      </header>
      <div>
        {$.isEmptyObject(weather) ? (
          <Oval color="#D9DEDB" />
        ) : (
          <WeatherCards weather={weather} />
        )}
      </div>
    </div>
  );
};

export default App;
