import React from "react";
//import ReactDOM from 'react';

import {
  Card,
  CardContent,
  CardActions,
  Button,
  Typography,
  Avatar,
} from "@mui/material";

class WeatherCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      list: [],
    };

    this.create = this.create.bind(this);
    this.update = this.update.bind(this);
    this.delete = this.delete.bind(this);
  }

  componentDidMount() {
    fetch(
      "api.openweathermap.org/data/2.5/forecast?q=Lisbon&appid=c9f6971381650cd6d35447c475cce567"
    )
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            list: result.list,
          });
        },
        (error) => {
          this.setState({
            error,
          });
        }
      );
  }

  create(e) {
    e.preventDefault();
  }

  update(e) {
    e.preventDefault();
  }

  delete(e) {
    e.preventDefault();
  }

  handleChange(changeObject) {
    this.setState(changeObject);
  }

  render() {
    const { error, list } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else {
      return (
        <div>
          <div>{list}</div>
          <Card sx={{ minWidth: 275 }}>
            <CardContent>
              <Typography
                sx={{ fontSize: 14 }}
                color="text.secondary"
                gutterBottom
              >
                Word of the day
              </Typography>
              <Typography variant="h5" component="div"></Typography>
              <Avatar alt="Weather image"></Avatar>
              <Typography variant="body2">
                well meaning and kindly.
                <br />
                {'"a benevolent smile"'}
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small">Learn More</Button>
            </CardActions>
          </Card>
        </div>
      );
    }
  }
}

export default WeatherCard;
