import React from "react";
import Axios from 'axios';
//import ReactDOM from 'react';

import {
  Card,
  CardContent,
  CardActions,
  Button,
  Typography,
  Avatar,
} from "@mui/material";

const getWeather = () =>{
  Axios.get(
    "https://api.openweathermap.org/data/2.5/forecast?q=Lisbon&appid=c9f6971381650cd6d35447c475cce567"
  ).then((response) => {
    this.setState({
      list: response.data.list
    })
  });
}

class WeatherCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      list: [],
    };

    this.create = this.create.bind(this);
    this.update = this.update.bind(this);
    this.delete = this.delete.bind(this);
  }

  create(e) {
    e.preventDefault();
  }

  update(e) {
    e.preventDefault();
  }

  delete(e) {
    e.preventDefault();
  }

  handleChange(changeObject) {
    this.setState(changeObject);
  }

  render() {
    return (
      <Card sx={{ minWidth: 275 }}>
        <CardContent>
          <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
            Word of the day
          </Typography>
          <Typography variant="h5" component="div"></Typography>
          <Avatar alt="Weather image"></Avatar>
          <Typography variant="body2">
            well meaning and kindly.
            <br />
            {'"a benevolent smile"'}
          </Typography>
        </CardContent>
        <CardActions>
          <Button size="small" onClick={getWeather}>Get Weather</Button>
        </CardActions>
      </Card>
    );
  }
}

export default WeatherCard;
