import React from "react";
import { Card, CardContent, Typography } from "@mui/material";

const WeatherCard = ({ weather }) => {

  const icon = `http://openweathermap.org/img/wn/${weather.list[0].weather[0].icon}@2x.png`

  const capitalize = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }
  
  return (
    <div>
      <Card sx={{ minWidth: 275 }}>
        <CardContent>
          <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
            {weather.city.name}
          </Typography>
          <Typography variant="h5" component="div"></Typography>
          <Typography variant="body2" sx={{ fontWeight: "bold" }}>{Math.round(weather.list[0].main.temp) + "ºC"}</Typography>
          <img alt="WeatherIcon" src = {icon}></img>
          <Typography variant="body2" >{capitalize(weather.list[0].weather[0].description)}</Typography>
        </CardContent>
      </Card>
    </div>
  );
};

export default WeatherCard;
