import './App.css';
import WeatherCard from './Components/weatherCard';
import { useEffect, useState } from 'react';

const App = () => {
  const [weather, setWeather] = useState([])
  const [icon, setIcon] = useState([])

  useEffect(() => {
    const getWeather = async () => {
      const weather = await fetchWeather()
      setWeather(weather)
      console.log('Icon Code: ${weather.list[0].weather[0].icon}')
    }

    const getIcon = async (weather) => {
      const icon = await fetchIcon(weather)
      setIcon(icon)

    }

    getWeather()
    getIcon(weather)
  }, [])

  const fetchWeather = async () => {
    const res = await fetch('http://api.openweathermap.org/data/2.5/forecast?q=Lisbon&appid=c9f6971381650cd6d35447c475cce567&units=metric')
    const data = await res.json()

    console.log(data)
    return data
  }

  const fetchIcon = async (weather) => {
    const res = await fetch('http://openweathermap.org/img/wn/' + weather.list[0].weather[0].icon + '@2x.png')
    const data = await res.json()

    return data
  }

  return (
    <div className="App">
    <header>
      <h1 className='Title'>WeatherApp</h1>
    </header>
    <div className='Hlist'>
      <ul>
        <li><WeatherCard weather={weather} icon={icon}/></li>
      </ul>
    </div>
    </div>
  );
}

export default App;
