import React from "react";
import Axios from "axios";

import {
  Card,
  CardContent,
  CardActions,
  Button,
  Typography,
  List,
  Avatar,
} from "@mui/material";

function assignAvatar() {}

class WeatherCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      city: null,
      list: [],
      temp: null,
      weatherIcon: null
    };
  }

  componentDidMount() {
    this.getWeather();
    this.assignIcon();
  }

  getWeather() {
    Axios.get(
      "https://api.openweathermap.org/data/2.5/forecast?q=Lisbon&appid=c9f6971381650cd6d35447c475cce567&units=metric"
    ).then((response) => {
      // console.log(response.data.list[0].weather[0].icon);
      this.setState({
        city: response.data.city.name,
        list: response.data.list,
        temp: response.data.list[0].main.temp,
        weatherIcon: response.data.list[0].weather[0].icon
      });
    });
  }

  assignIcon() {
    const icon = this.state.weatherIcon;
    console.log(icon);

    var url = 'http://openweathermap.org/img/wn/${icon}@2x.png';
    console.log(url);
  }

  render() {
    return (
      <div>
        <Card sx={{ minWidth: 275 }}>
          <CardContent>
            <Typography
              sx={{ fontSize: 14 }}
              color="text.secondary"
              gutterBottom
            >
              {this.state.city}
            </Typography>
            <Typography variant="h5" component="div"></Typography>
            <Typography variant="body2">{this.state.temp}</Typography>
            <img src=""></img>
          </CardContent>
        </Card>
      </div>
    );
  }
}

export default WeatherCard;
