import "./App.css";
import WeatherCards from "./Components/weatherCards";
import { useEffect, useState } from "react";
import $ from "jquery";
import { CountryDropdown, RegionDropdown } from "react-country-region-selector";
import { Oval } from "react-loader-spinner";

const App = () => {
  const [weather, setWeather] = useState({});
  const [country, setCountry] = useState('');
  const [region, setRegion] = useState('');
  const [time, setTime] = useState(0);

  useEffect(() => {
    if(region === ''){
      const getWeather = async () => {
        const weatherApi = await fetchWeather();
        setWeather(weatherApi);
      };
      getWeather();
    }


    const interval = setInterval(() => {
      setTime(time + 1);
    }, 350000);
    return () => clearInterval(interval);
  }, [time, region]);

  const fetchWeather = async () => {
    const res = await fetch(
      `http://api.openweathermap.org/data/2.5/forecast?q=${region}&appid=c9f6971381650cd6d35447c475cce567&units=metric`
    );
    const data = await res.json();
    console.log(data);
    return data;
  };


  return (
    <div className="App">
      <header>
        <h1 className="Title">WeatherApp</h1>
        <div>
          <CountryDropdown value={country} onChange={(val) => setCountry(val)}/>
          <RegionDropdown country={country} value={region} onChange={(val) => setRegion(val)}/>
        </div>

      </header>
      <div>
        {$.isEmptyObject(weather) ? (
          <Oval color="#D9DEDB" />
        ) : (
          <WeatherCards weather={weather} />
        )}
      </div>
    </div>
  );
};

export default App;
