import React from "react";
import Axios from 'axios';


import {
  Card,
  CardContent,
  CardActions,
  Button,
  Typography,
  List,
  Avatar,
} from "@mui/material";

function assignAvatar(){

}


class WeatherCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      city: null,
      list: [],
    };
  }

  componentDidMount(){
    this.getWeather();
  }

  getWeather() {
    Axios.get("https://api.openweathermap.org/data/2.5/forecast?q=Lisbon&appid=c9f6971381650cd6d35447c475cce567&units=metric"
    ).then((response) => {
      console.log(response)
      console.log(response.data.list[0].main)
      this.setState({
        city: response.data.city.name,
        list: JSON.Parse(response.data.list)
      })
    })
  }

  render() {
    return (
      <Card sx={{ minWidth: 275 }}>
        <CardContent>
          <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
            {this.state.city}
          </Typography>
          <Typography variant="h5" component="div"></Typography>
          <List variant="body2">
              {this.state.list[0].main.temp}
          </List>
        </CardContent>
      </Card>
    );
  }
}



export default WeatherCard;
