class WeatherCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      city: null,
      list: [],
    };
  }

  componentDidMount(){
    this.getWeather();
  }

  getCity(){
    
  }

  getWeather() {
    Axios.get("https://api.openweathermap.org/data/2.5/forecast?q=Lisbon&appid=c9f6971381650cd6d35447c475cce567"
    ).then((response) => {
      console.log(response.data.list)
      this.setState({
        city: response.data.city.name,
        list: response.data.list
      })
    })
  }

  render() {
    return (
      <Card sx={{ minWidth: 275 }}>
        <CardContent>
          <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
            {this.state.city}
          </Typography>
          <Typography variant="h5" component="div"></Typography>
          <Avatar alt="Weather image"></Avatar>
          <Typography variant="body2">
            well meaning and kindly.
            <br />
            {'"a benevolent smile"'}
          </Typography>
        </CardContent>
        <CardActions>
          <Button size="small">Get Weather</Button>
        </CardActions>
      </Card>
    );
  }
}



export default WeatherCard;
